select o.symbol,o.strike_price,o.bid,o.ask,o.last,o.mark,o.expiration_date,trim(f.description),f."regularMarketLastPrice",f.volatility,(f.volatility * "regularMarketLastPrice") + "regularMarketLastPrice" high_end,"regularMarketLastPrice" - (f.volatility * "regularMarketLastPrice")  low_end from options_pro o,sp500 f where o.datetime = current_date    and o.datetime = f.datetime and o.volatility > 50 and strike_price >= 20 and strike_price < 90 and type = 'PUT' and o.stock_symbol = f.symbol and bid > 1 and strike_price < "regularMarketLastPrice" and expiration_date < current_date + 18 order by o.bid / o.strike_price ,  mark desc;

